import org.omg.CORBA.ORB;

/**
 * Implementation of a test object that can be "ping()-ed"
 *
 * @author Gerald Brose
 */

public class TestObjectImpl
    extends TestObjectPOA
{
    public TestObjectImpl()
    {}

    public void ping()
    {}

    public A[] testMarshall()
    {
        A[] result = new A[2];

        // This will simulate starting and stopping an A server.
        result[0] = AHelper.narrow ((ORB.init( new String[0], null)).string_to_object ("IOR:" ) );
        result[1] = BHelper.narrow ((ORB.init( new String[0], null)).string_to_object ("IOR:" ) );

        return result;
    }
}
